mapper.select:
SELECT *
FROM novel_story_memory
WHERE project_id = #{projectId}
  AND memory_type = #{memoryType}
ORDER BY story_timeline DESC
LIMIT #{limit}

SELECT *
FROM novel_story_memory
WHERE project_id = #{projectId}
  AND importance_score >= #{minScore}
ORDER BY importance_score DESC, story_timeline DESC
LIMIT #{limit}

SELECT * FROM novel_story_memory WHERE id IN
#{id}
AND is_deleted = 0 ORDER BY story_timeline DESC, importance_score DESC

SELECT * FROM novel_story_memory
WHERE project_id = #{projectId}
AND is_deleted = 0

    AND memory_type = #{memoryType}

ORDER BY story_timeline DESC, importance_score DESC

SELECT * FROM novel_story_memory
WHERE project_id = #{projectId}
  AND is_deleted = 0
  AND story_timeline BETWEEN #{startTimeline} AND #{endTimeline}
ORDER BY story_timeline ASC, importance_score DESC
